    thumbnailPath: string,
    maxRetries: number,
    size: string
  ): Promise<string | null> => {
    for (let attempt = 1; attempt <= maxRetries; attempt++) {
      try {
        const thumbnailRef = ref(storage, thumbnailPath);
        const URL = await getDownloadURL(thumbnailRef);
        console.log(`‚úÖ Thumbnail ${size} ready after ${attempt} attempts`);
        return URL;
      } catch (error: any) {
        if (attempt < maxRetries) {
          // ƒê·ª£i l√¢u h∆°n cho m·ªói l·∫ßn retry (2s, 3s, 4s, ...)
          const waitTime = 2000 + (attempt * 1000);
          console.log(`‚è≥ Waiting for thumbnail ${size}... attempt ${attempt}/${maxRetries}`);
          await new Promise(resolve => setTimeout(resolve, waitTime));
        } else {
          console.log(`‚ö†Ô∏è Thumbnail ${size} not available after ${maxRetries} attempts (Extension may still be processing)`);
          return null;
        }
      }
    }
    return null;
  };

